using Project.EntenEller.Base.Scripts.Advanced.GameObjects;using Project.EntenEller.Base.Scripts.Cache.Components.Master;using Project.Scripts.Item;using UnityEngine;namespace Project.Scripts.Cell{    public class CellInventory : EEBehaviour    {        [SerializeField] private CellVisualizer cellVisualizer;        [HideInInspector] public ItemData Item;        public bool isActive;        private EEGameObject eeGameObject;        protected override void EEAwake()        {            base.EEAwake();            eeGameObject = gameObject.GetEEGameObject();        }        public void Active(ItemData itemData)        {            Item = itemData;            isActive = true;            cellVisualizer.ShowCell(itemData);            gameObject.SetActive(true);        }        public void Disable()        {            eeGameObject.SetState(false);            isActive = false;        }    }}