using Project.EntenEller.Base.Scripts.Advanced.GameObjects;using Project.EntenEller.Base.Scripts.Cache.Components.Master;using Project.Scripts.Item;using UnityEngine;namespace Project.Scripts.Cell{    public class CellInventory : EEBehaviour    {        [SerializeField] private CellVisualizer cellVisualizer;        [HideInInspector] public ItemData Item;        public bool isActive;        public void Active(ItemData itemData)        {            Item = itemData;            isActive = true;            cellVisualizer.ShowCell(itemData);            gameObject.SetActive(true);        }        public void Disable()        {            Item = null;            isActive = false;            gameObject.SetActive(false);        }    }}