using MPUIKIT;using Project.EntenEller.Base.Scripts.Advanced.Transforms.RectTransforms;using Project.EntenEller.Base.Scripts.Cache.Components.Master;using Project.Scripts.Item;using UnityEngine;namespace Project.Scripts.Cell{    public class CellVisualizer : EEBehaviour    {        [SerializeField] private MPImage image;        [SerializeField] private float defaultSize = 150;        public void ShowCell(ItemData itemData)        {            image.sprite = itemData.Icon;            image.color = Color.white;            GetSelf<RectTransform>().SetSizeWithCurrentAnchors(RectTransform.Axis.Vertical, defaultSize * itemData.SizeY);        }        public void HideCell()        {            image.color = new Color(0,0,0,0);            GetSelf<RectTransform>().SetSizeWithCurrentAnchors(RectTransform.Axis.Vertical,defaultSize);        }    }}