using Project.EntenEller.Base.Scripts.Cache.Components.Master;using Project.EntenEller.Base.Scripts.UI.Text;using Project.Scripts.CCLib.Text;using UnityEngine;namespace Project.Scripts.Days{    public class DayTextWriter : EEBehaviour    {        [SerializeField] private EEText numberDayOnePageText;        [SerializeField] private EEText numberDayTwoPageText;        [SerializeField] private CCTextPaginator textPaginatorDescriptionOldDay;        [SerializeField] private CCTextPaginator textPaginatorDescriptionEventDay;        public void SetDayTextInMagazine(DayData dayData)        {            numberDayTwoPageText.SetData(dayData.NumberDay);            numberDayOnePageText.SetData(dayData.NumberDay);            textPaginatorDescriptionOldDay.SetText(dayData.DescriptionOldDay);            textPaginatorDescriptionEventDay.SetText(dayData.DescriptionEventDay);        }    }}