using Project.EntenEller.Base.Scripts.Cache.Components.Master;using Project.EntenEller.Base.Scripts.Patterns.Singleton;using UnityEngine;namespace Project.Scripts.FirstScene{    [ExecutionOrder(-999)]    public class FirstSceneInitilization : EEBehaviour    {        [SerializeField] private bool isMovePlayerAtStart;        private GlobalSettings globalSettings;        protected override void EEAwake()        {            base.EEAwake();            globalSettings = EESingleton.Get<GlobalSettings>();        }        public void InitPlayer()        {            if (isMovePlayerAtStart) globalSettings.StartAnyMovePlayer();            else globalSettings.StopAnyMovePlayer();        }    }}