using System;using System.Collections.Generic;using System.Linq;using Project.EntenEller.Base.Scripts.Advanced.Notifiers;using Project.Scripts.Item;namespace Project.Scripts.Inventory.Base{    [Serializable]    public class Inventory    {        public int Size = 4;        public List<ItemStack> Items  = new List<ItemStack>();        public InventoryAdder Adder;        public InventoryRemover Remover;        public EENotifier UpdateAmountItemNotifier, AddNewItemNotifier;        public EENotifier RemoveItemNotifier, DecreaseAmountItemNotifier;        public Inventory()        {            Adder = new InventoryAdder(this);            Remover = new InventoryRemover(this);        }        public int GetAmountItem()        {            var sum = Items.Sum(itemStack => itemStack.Amount * itemStack.Item.SizeY);            return sum;        }    }}