using System;using Project.Scripts.CrossScene;using UnityEngine;namespace Project.Scripts.Inventory.Base{    [Serializable]    public class InventoryAdderToCrossScene    {        [SerializeField] private CrossSceneData crossSceneData;        private readonly Inventory inventory;        public InventoryAdderToCrossScene(Inventory inventory)        {            this.inventory = inventory;        }        public void AddAllItemToCrossScene(bool isRemove = false)        {            foreach (var itemStack in inventory.Items)            {                crossSceneData.AddItemStack(itemStack);            }            if (isRemove) inventory.Remover.RemoveAll();        }    }}