using System.Linq;using Project.Scripts.Item;namespace Project.Scripts.Inventory{    public class InventoryAdder    {        private readonly Inventory inventory;        public InventoryAdder(Inventory inventory)        {            this.inventory = inventory;        }        public void AddItem(ItemData itemData, int amount = 1)        {            foreach (var item in inventory.Items.Where(item => item.Item.ID == itemData.ID))            {                item.Amount += amount;                inventory.UpdateAmountItemNotifier.Notify();                return;            }            var newItem = new ItemStack {Item = itemData, Amount = amount};            inventory.Items.Add(newItem);            inventory.AddNewItemNotifier.Notify();        }    }}